import Schedule from "../JsModules/Schedule";
import "./componentStyles/editSchedule.css";

// what's in props?
// props.app : the app model responsible for creating, updating, and deleting scheduels
// contains:
// currentSchedule: the schedule currently being shown by the UI
// app.updateSchedule(id, schedule)
// updateEditor: gives ability to close display

function EditSchedule(props) {
  const handleSubmit = () => {
    // close the menu
    props.updateEditor();
  };

  const handleChange = (e) => {
    // loop through courses, if the course matches with the one being edited, update that?
    console.log("event that occured", e);
    console.log("where the edit happend", e.target);
    console.log("course being edited", e.target.classList[0]);
    console.log("change made", e.target.value);

    // NOTE: add className of the course name to all inputs
    // NOTE: need to identify which course attribute was edited, and update the Course object
    // NOTE: afterwards, pressing done will update the schedule itself (locally and on the server)
  };

  return (
    <div className="editor-container">
      {props.currentSchedule ? (
        <div className="inputs-holder">
          <input
            className="header-input"
            type="text"
            defaultValue={props.currentSchedule.name}
            id="scheduleName"
          />
          <ul className="schedules-list">
            {props.currentSchedule.courses.map((course, index) => (
              <div className="course-edit-container" key={index}>
                <h3>{course.courseName}</h3>
                <div className="course-boxes">
                  <div className="box box-1">
                    <label htmlFor="">Course Name</label>
                    <input
                      id={course.courseName}
                      type="text"
                      defaultValue={course.courseName}
                      onChange={handleChange}
                      className={course.courseName}
                    />

                    <label htmlFor="">Course Code</label>
                    <input
                      type="text"
                      defaultValue={course.courseCode}
                      id={course.courseCode}
                    />

                    <label htmlFor="">Section Number</label>
                    <input
                      type="text"
                      defaultValue={course.sectionNumber}
                      id={course.sectionNumber}
                    />

                    <label htmlFor="">Credit Hours</label>
                    <input
                      type="text"
                      defaultValue={course.creditHours}
                      id={course.creditHours}
                    />
                  </div>
                  <div className="box box-2">
                    <label htmlFor="">Meet Days</label>
                    <input
                      type="text"
                      defaultValue={course.meetFrequencies}
                      id={course.meetFrequencies}
                    />

                    <label htmlFor="">Meet Time</label>
                    <input
                      type="text"
                      defaultValue={course.meetTime}
                      id={course.meetTime}
                    />

                    <label htmlFor="">Meet Room</label>
                    <input
                      type="text"
                      defaultValue={course.meetRoom}
                      id={course.meetRoom}
                    />

                    <label htmlFor="">Professor</label>
                    <input
                      type="text"
                      defaultValue={course.professor}
                      id={course.professor}
                    />
                  </div>
                </div>
                <span className="divider"></span>
              </div>
            ))}
          </ul>
          <div className="schedule-options">
            <button className="schedule-edit-btn">Add</button>
            <button onClick={handleSubmit} className="schedule-edit-btn">
              Done
            </button>
          </div>
        </div>
      ) : (
        <p>No Schedule Found.</p>
      )}
    </div>
  );
}

export default EditSchedule;
