import { auth } from "../firebase";
import { database } from "../firebase";
import Schedule from "../JsModules/Schedule";
import Course from "../JsModules/Course";

// firebase instance
import firebase from "firebase/compat/app";

function exampleData() {
  // this file shows how to send information to the firebase database.  I will execute it soon and then keep working on the UI.
  var course = new Course(
    "Math101",
    "MTH101",
    "01",
    "4.0",
    "M W F",
    "2:00PM",
    "Batelle 101",
    "Dr. Arrey"
  );

  var schedule = new Schedule("Math Department");
  schedule.addCourse(course);

  // var user = auth.currentUser;
  // var userId = user.uid;
  // var userRef = database.ref("users/" + userId);
  const usersRef = firebase.firestore().collection("users");
  const userRef = usersRef.get(firebase.auth().currentUser);
  userRef
    .update({
      schedules: [
        {
          name: schedule.name,
          courses: [
            {
              name: course.courseName,
              courseCode: course.courseCode,
              sectionNumber: course.sectionNumber,
              creditHours: course.creditHours,
              meetFrequency: course.meetFrequencies,
              meetingTime: course.meetTime,
              meetRoom: course.meetingRoom,
              professor: course.professor,
            },
            {
              name: "History 201",
              professor: "Dr. Smith",
              days: ["Tuesday", "Thursday"],
              time: "1:00pm - 2:30pm",
            },
          ],
        },
      ],
    })
    .then(() => {
      console.log("success");
    })
    .catch((error) => {
      console.log("Error creating course: ", error);
    });
}

export default exampleData;
